def ensure_database_ready():
    """Ensure database exists and is populated"""
    try:
        # Check if database file exists, if not create it
        if os.environ.get('VERCEL'):
            db_path = '/tmp/db.sqlite3'
            if not os.path.exists(db_path):
                print(f"Creating database at {db_path}")
                open(db_path, 'a').close()
        
        # Run migrations
        print("Running migrations...")
        execute_from_command_line(['manage.py', 'migrate', '--noinput'])
        
        # Check if tables exist by trying to access them
        from faal.models import Quote, HafezGhazal
        
        # Test database connection and table existence
        try:
            ghazal_count = HafezGhazal.objects.count()
            quote_count = Quote.objects.count()
            print(f"Database ready: {ghazal_count} ghazals, {quote_count} quotes")
        except Exception as e:
            print(f"Database tables not ready, creating: {e}")
            execute_from_command_line(['manage.py', 'migrate', '--run-syncdb'])
            
        # Populate if empty
        if HafezGhazal.objects.count() == 0:
            populate_from_excel_data()
            
    except Exception as e:
        print(f"Database initialization error: {e}")
        raise

